    private void btnAñadirCuadradoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAñadirCuadradoActionPerformed
        int xr1 = Integer.parseInt(txtCoordenada1EnX.getText());
        int yr1 = Integer.parseInt(txtCoordenada1EnY.getText());
        int xr3 = Integer.parseInt(txtCoordenada3EnX.getText());
        int yr3 = Integer.parseInt(txtCoordenada3EnY.getText());
        Cuadrado rectangulo = new Cuadrado(new Punto(xr1, yr1), new Punto(xr3, yr3));
        rectangulo.nombre = txtNombreDelRectangulo.getText();
        Graficador.agregar(rectangulo);
        this.txtPresentar.append(rectangulo.toString() + "\n");
        txtCoordenada1EnX.setText("");
        txtCoordenada1EnY.setText("");
        txtCoordenada3EnX.setText("");
        txtCoordenada3EnY.setText("");
        txtNombreDelRectangulo.setText("");

    }//GEN-LAST:event_btnAñadirCuadradoActionPerformed

