    private void jbtAñadirCirculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtAñadirCirculoActionPerformed
        int xc = Integer.parseInt(txtCoordenadaEnX.getText());
        int yc = Integer.parseInt(txtCoordenadaEnY.getText());
        double radio = Double.parseDouble(txtRadio.getText());
        Circulo circulo = new Circulo();
        circulo.radio = radio;
        circulo.centro = new Punto(xc, yc);
        circulo.nombre = txtNombreDelCirculo.getText();
        Graficador.agregar(circulo);
        this.txtPresentar.append(circulo.toString() + "\n");
        txtCoordenadaEnX.setText("");
        txtCoordenadaEnY.setText("");
        txtRadio.setText("");
        txtNombreDelCirculo.setText("");

    }//GEN-LAST:event_jbtAñadirCirculoActionPerformed

    private void btnMoverFigurasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMoverFigurasActionPerformed
        int dx = Integer.parseInt(txtMoverEnX.getText());
        int dy = Integer.parseInt(txtMoverEnY.getText());
        Graficador.moverFiguras(dx, dy);
        Graficador.grabar();
        txtMoverEnX.setText("");
        txtMoverEnY.setText("");
    }//GEN-LAST:event_btnMoverFigurasActionPerformed

    private void jbtVisualizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbtVisualizarActionPerformed
        String datos = Graficador.leer();
        this.txtPresentar1.append(datos);
    }//GEN-LAST:event_jbtVisualizarActionPerformed

