    private void btnAñadirTrianguloActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAñadirTrianguloActionPerformed
        int xt1 = Integer.parseInt(txtCoordenada1X.getText());
        int yt1 = Integer.parseInt(txtCoordenada1Y.getText());
        int xt2 = Integer.parseInt(txtCoordenada2X.getText());
        int yt2 = Integer.parseInt(txtCoordenada2Y.getText());
        int xt3 = Integer.parseInt(txtCoordenada3X.getText());
        int yt3 = Integer.parseInt(txtCoordenada3Y.getText());
        Triangulo triangulo = new Triangulo(new Punto(xt1, yt1), new Punto(xt2, yt2), new Punto(xt3, yt3));
        triangulo.nombre = txtNombreDelTriangulo.getText();
        Graficador.agregar(triangulo);
        this.txtPresentar.append(triangulo.toString() + "\n");
        txtCoordenada1X.setText("");
        txtCoordenada1Y.setText("");
        txtCoordenada2X.setText("");
        txtCoordenada2Y.setText("");
        txtCoordenada3X.setText("");
        txtCoordenada3Y.setText("");
        txtNombreDelTriangulo.setText("");
    }//GEN-LAST:event_btnAñadirTrianguloActionPerformed

